        public static async Task SendEmailAsync(string to, string subject, string header, string body, string footer, string from, SecureString appPassword, string date, CancellationToken cancellationToken = default)
        {
            try
            {
                using var stream = Assembly.GetEntryAssembly()?.GetManifestResourceStream($"MeebookConnector.Resources.NotificationMail.html")!;
                using var streamReader = new StreamReader(stream, Encoding.UTF8);
                string mailTemplate = streamReader.ReadToEnd().Trim();

                string mailBody = mailTemplate
                        .Replace("[HEADER]", header)
                        .Replace("[CONTENT]", body)
                        .Replace("[DATE]", date)
                        .Replace("[FOOTER]", footer);


                using (var client = new SmtpClient("smtp.gmail.com", 587))
                {
                    client.EnableSsl = true;
                    client.Credentials = new NetworkCredential(from, appPassword);

                    //var mail = new MailMessage(from, to, subject, null)
                    //{
                    //    IsBodyHtml = true
                    //};
                    var mail = new MailMessage()
                    {
                        To = { new MailAddress(to) },
                        From = new MailAddress(from),
                        Subject = subject,
                        IsBodyHtml = true
                    };
                    
                    // Burning school logo
                    string imageBase64 = ""; // Hardcoded img - read into memory stream

                    byte[] imageBytes = Convert.FromBase64String(imageBase64);
                    MemoryStream image = new MemoryStream(imageBytes);
                    AlternateView alternateView = AlternateView.CreateAlternateViewFromString(mailBody, null, System.Net.Mime.MediaTypeNames.Text.Html);

                    LinkedResource headerImage = new LinkedResource(image, System.Net.Mime.MediaTypeNames.Image.Webp);
                    headerImage.ContentId = "mailIcon";
                    headerImage.ContentType = new ContentType("image/webp");
                    alternateView.LinkedResources.Add(headerImage);

                    mail.AlternateViews.Add(alternateView);

                    await client.SendMailAsync(mail, cancellationToken);

                    Console.WriteLine($"Mail '{subject}' sent successfully to '{to}'");
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine("Failed to send mail. " + ex.Message);                                
            }
        }

        public static string BuildHtmlBody(List<AulaPlanItem> items, string header, string subHeader, string name, HashSet<string> changedItemIds, HashSet<string> noChanges, string summary, List<CalenderEvent> calenderEvents)
        {
            // Find uge (første item i listen)
            if (items == null || items.Count == 0)
                return "<p>Ingen data</p>";

            var monday = items.Min(i => i.Date).Date;
            while (monday.DayOfWeek != DayOfWeek.Monday)
                monday = monday.AddDays(-1);

            var days = Enumerable.Range(0, 5).Select(offset => monday.AddDays(offset)).ToList();

            var sb = new StringBuilder();

            //sb.AppendLine("<html><head><meta charset=\"UTF-8\"></head><body>");
            //sb.AppendLine("<h2 style=\"font-family:Arial,sans-serif;color:#3B7D6F; margin-bottom:0px;\">" + header + "</h2>");
            //sb.AppendLine("<span style=\"font-family:Arial,sans-serif;color:#3B7D6F;padding-left: 36px;\">" + subHeader + "</span>");
            sb.AppendLine($"<h4 style=\"font-family:Arial,sans-serif;color:#3B7D6F;\">Ugens vigtigste opgaver for {name}:</h4>");
            sb.AppendLine("<p style=\"font-family:Arial,sans-serif; padding-left: 10px;\">" + summary + "</p>");
            sb.AppendLine($"<h4 style=\"font-family:Arial,sans-serif;color:#3B7D6F;\">Meebook ugeplan:</h4>");
            sb.AppendLine("<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\" style=\"width:100%; border-collapse:collapse; font-family:Arial,sans-serif; font-size:14px;\">");

            // header
            sb.AppendLine("<tr>");
            foreach (var day in days)
            {
                sb.AppendFormat(
                    "<th align=\"left\" valign=\"top\" style=\"border:1px solid #ccc; padding:8px; background-color:#3B7D6F; color:#ffffff; min-width:150px;\">{0}</th>",
                    Helpers.CapitalizeFirstLetter(day.ToString("dddd", new System.Globalization.CultureInfo("da-DK")))
                );
            }
            sb.AppendLine("</tr>");

            // content row
            sb.AppendLine("<tr>");
            foreach (var day in days)
            {
                var dayItems = items.Where(i => i.Date.Date == day.Date).ToList();

                sb.Append("<td valign=\"top\" style=\"border:1px solid #ccc; padding:8px;\">");

                if (dayItems.Count > 0)
                {
                    foreach (var i in dayItems)
                    {
                        var background = "#d4d5d9";// changedItemIds.Contains(i.Id) ? "#fff3cd" : "#f0f4fa"; // gul hvis ændret
                        //if (noChanges.Contains(i.Id))
                        //{
                        //    background = "#e2e3e5"; // grå hvis ingen ændringer
                        //}
                        var color = "#3B7D6F";// changedItemIds.Contains(i.Id) ? "#856404" : "#3B7D6F";

                        string icon = i.ItemType switch
                        {
                            PlanItemType.Assignment => "📝",
                            PlanItemType.Task => "📚",
                            PlanItemType.Comment => "💬",
                            _ => "📖",
                        };                        

                        sb.AppendFormat(
                            $"<div style=\"background-color:{background}; color:{color}; font-weight:bold; padding:4px; margin-bottom:4px;\">{System.Net.WebUtility.HtmlEncode(i.Subject + " " + icon)}</div>"
                        );

                        if (!string.IsNullOrWhiteSpace(i.Text))
                        {
                            sb.AppendFormat(
                                "<div style=\"margin-bottom:8px; color:#333333;\">{0}</div>",
                                System.Net.WebUtility.HtmlEncode(i.Text)
                            );
                        }

                        if (!string.IsNullOrWhiteSpace(i.Link))
                        {
                                                       sb.AppendFormat(
                                "<div style=\"margin-bottom:8px;\"><a href=\"{0}\" target=\"_blank\">Link til opgave/materiale</a></div>",
                                System.Net.WebUtility.HtmlEncode(i.Link)
                            );
                        }
                    }
                }
                else
                {
                    sb.Append("<div style=\"color:#999999;\">Ingen timer</div>");
                }

                sb.Append("</td>");
            }
            sb.AppendLine("</tr>");

            sb.AppendLine("</table>");
            
            //sb.AppendLine("<p style=\"font-weight:bold; padding:4px;\">Forklaring:</p>");
            sb.AppendLine("<ul style=\"color:#333333; font-family:Arial,sans-serif; font-size:12px; padding-left: 20px;\">");
            sb.AppendLine("<li>💬 Kommentar</li>");
            sb.AppendLine("<li>📚 Opgave</li>");
            sb.AppendLine("<li>📝 Aflevering</li>");
            //sb.AppendLine("<li style=\"background-color:#f0f4fa; color:#3B7D6F; font-weight:bold; padding:4px; margin-bottom:4px; width:150px;\">Ny</li>");
            //sb.AppendLine("<li style=\"background-color:#fff3cd; color:#856404; font-weight:bold; padding:4px; margin-bottom:4px; width:150px;\">Ændret</li>");
            //sb.AppendLine("<li style=\"background-color:#e2e3e5; color:#333333; font-weight:bold; padding:4px; margin-bottom:4px; width:150px;\">Ingen ændringer</li>");            
            sb.AppendLine("</ul>");

            if (calenderEvents.Count > 0)
            {
                sb.AppendLine($"<h4 style=\"font-family:Arial,sans-serif;color:#3B7D6F;\">Vigtige datoer:</h4>");
                sb.AppendLine("<ul style=\"font-family:Arial,sans-serif; padding-left: 20px;\">");
                foreach (var calenderEvent in calenderEvents)
                {
                    sb.AppendLine("<li>");
                    sb.AppendLine($"<span style=\"color:#3B7D6F;\"><strong>{System.Net.WebUtility.HtmlEncode(calenderEvent.Title)}</strong></span>");
                    sb.AppendLine($"<br/><span>{Helpers.CapitalizeFirstLetter(calenderEvent.StartDateTime.ToString("dddd", new System.Globalization.CultureInfo("da-DK")))} {calenderEvent.StartDateTime.ToString("dd/MM/yyyy HH:mm")} - {(calenderEvent.StartDateTime.DayOfYear == calenderEvent.EndDateTime.DayOfYear ? calenderEvent.EndDateTime.ToString("HH:mm") : calenderEvent.EndDateTime.ToString("dd/MM/yyyy HH:mm"))} </span>");
                    sb.AppendLine("</li>");
                }
                sb.AppendLine("</ul>");
            }
            return sb.ToString();
        }

